wfrp4e.script:
let stunned = args.actor.hasCondition("stunned")
if (stunned)
   setProperty(stunned.data, "flags.wfrp4e.script", "")

let poisoned= args.actor.hasCondition("poisoned")
if (poisoned)
   setProperty(poisoned.data, "flags.wfrp4e.script", "")

let deafened = args.actor.hasCondition("deafened")
if (deafened)
   setProperty(deafened.data, "flags.wfrp4e.effectTrigger", "")

let entangled = args.actor.hasCondition("entangled")
if (entangled)
   setProperty(entangled.data, "flags.wfrp4e.effectTrigger", "")

let fatigued = args.actor.hasCondition("fatigued")
if (fatigued)
   setProperty(fatigued.data, "flags.wfrp4e.script", "")

let blinded = args.actor.hasCondition("blinded")
if (blinded)
   setProperty(blinded.data, "flags.wfrp4e.effectTrigger", "")

let broken = args.actor.hasCondition("broken")
if (broken)
   setProperty(broken.data, "flags.wfrp4e.effectTrigger", "")

let prone= args.actor.hasCondition("prone")
if (prone)
   setProperty(prone.data, "flags.wfrp4e.effectTrigger", "")

let broken = args.actor.hasCondition("broken")
if (broken)
    args.actor.removeCondition("broken", broken.flags.wfrp4e.value)

let wounds = args.actor.status.wounds

if (wounds.value < wounds.max)
   args.actor.update({"data.status.wounds.value" : wounds.value + 1})

if (args.actor.has("Ożywieniec"))
{
    let damage = (await new Roll("1d10").roll()).total
    args.actor.applyBasicDamage(damage, {damageType : game.wfrp4e.config.DAMAGE_TYPE.IGNORE_ALL})
}

let damage = (await new Roll("1d10").roll()).total
    args.actor.applyBasicDamage(damage, {damageType : game.wfrp4e.config.DAMAGE_TYPE.IGNORE_ALL})

if (args.actor.has("Ożywieniec") || args.actor.has("Demoniczny"))
{
  args.actor.addCondition("ablaze")
}

let num = 1 + {{overcast.usage.other.count}}
if (!num || num <= 0)
   num = 1

args.actor.addCondition("entangled", num)

await fromUuid("Compendium.wfrp4e-core.traits.pLW9SVX0TVTYPiPv").then(item => {
   item = item.toObject()
   item.data.specification.value = 3
   this.actor.createEmbeddedDocuments("Item", [item])
})

fromUuid("Compendium.wfrp4e-core.traits.AtpAudHA4ybXVlWM").then(item => {
   item = item.toObject()
    item.data.specification.value = 4
   this.actor.createEmbeddedDocuments("Item", [item])
})

if (args.type == "characteristic" && args.item == "int")
                    {
                            args.prefillModifiers.modifier += 20
                    }
                    else if (args.type == "skill")
                    {
                        if (args.item.characteristic.key == "int")
                            args.prefillModifiers.modifier += 20
                    }
