wfrp4e.script:
if (args.type == "skill" && (args.item.name == "Wspinaczka"  || args.item.name.includes("Skradanie")))
{
   let sl = this.actor.characteristics.ag.bonus
   args.prefillModifiers.slBonus += sl;
}

let AP = args.actor.status.armour
let APdamage = this.item.getFlag('wfrp4e', 'APdamage') || {}

let value = parseInt(this.item.specification.value)

if (Number.isNumeric(value))
{
AP["head"].value += (value - (APdamage.head || 0))
AP["rArm"].value += (value - (APdamage.rArm|| 0))
AP["lArm"].value += (value - (APdamage.lArm || 0))
AP["body"].value += (value - (APdamage.body || 0))
AP["lLeg"].value += (value - (APdamage.lLeg || 0))
AP["rLeg"].value += (value - (APdamage.rLeg || 0))
}

let value = parseInt(this.item.specification.value)
let name = this.actor.data.token.name

if (game.user.isGM && game.user.targets.size)
{
  game.user.targets.forEach(t => {
    t.actor.applyFear(value, name)
  })
  game.user.updateTokenTargets([]);
}
else 
{
  game.wfrp4e.utility.postFear(value, name)
}

let damage = (await new Roll("1d10").roll()).total
    args.actor.applyBasicDamage(damage, {damageType : game.wfrp4e.config.DAMAGE_TYPE.IGNORE_ALL})

args.actor.addCondition("deafened", 3)

args.actor.setupSkill("Odporność", {absolute : {difficulty: "average" }, context : {failure: "Gana un Estado de Quebrantado", success : "Evitado el Estado de Quebrantado"}}).then(setupData => {
   args.actor.basicTest(setupData).then(test => {
      if (test.result.outcome == "failure")
         args.actor.addCondition("broken")
   })
})

let chatData = {content: "", whisper: ChatMessage.getWhisperRecipients("GM")}

let wounds = duplicate(this.actor.status.wounds)
let regen = (await new Roll("1d10").roll()).total

if (wounds.value >= wounds.max)
   return

if (wounds.value > 0)
{
   wounds.value += regen
   if (wounds.value > wounds.max) 
        wounds.value = wounds.max
   chatData.content += `<b>${this.actor.name}</b> recupera ${regen} Heridas.`

    if (regen == 10)
       chatData.content += `<br>Adicionalmente, regenera una Herida Crítica.`
}
else if (regen >= 8)
{
    chatData.content += `<b>${this.actor.name}</b> sacó un ${regen} y recupera 1 Herida.`
    wounds.value += 1
    if (regen == 10)
       chatData.content += `<br>Adicionalmente, regenera una Herida Crítica.`
}
else
{
    chatData.content += `<b>${this.actor.name}</b> Tirada de Regenerar de ${regen} - Sin efecto.`
}

this.actor.update({"data.status.wounds" : wounds})
ChatMessage.create(chatData)

if (args.type == "skill" &&  args.item.name.includes("Skradanie"))
{
   let sl = this.actor.characteristics.ag.bonus
   args.prefillModifiers.slBonus += sl;
}

let actorSize = game.wfrp4e.config.actorSizeNums[args.actor.details.size.value]
let attackerSize = game.wfrp4e.config.actorSizeNums[args.attacker.details.size.value]

if (attackerSize > actorSize)
{
   args.actor.addCondition("prone")
   ChatMessage.create({content: `<b>Coletazo</b>: ${args.actor.data.token.name} está ahora @Condition[Tumbado]`, speaker : {alias: args.attacker.data.token.name}})
}

let actorSize = game.wfrp4e.config.actorSizeNums[args.actor.details.size.value]
let attackerSize = game.wfrp4e.config.actorSizeNums[args.attacker.details.size.value]

if (attackerSize > actorSize)
{
   args.actor.addCondition("entangled")
   ChatMessage.create({content: `<b>Ataque de lengua</b>: ${args.actor.data.token.name} está ahora @Condition[Enmarañado]`, speaker : {alias: args.attacker.data.token.name}})
}

let actorSize = game.wfrp4e.config.actorSizeNums[args.actor.details.size.value]
let attackerSize = game.wfrp4e.config.actorSizeNums[args.attacker.details.size.value]

if (attackerSize > actorSize)
{
   args.actor.addCondition("entangled")
   ChatMessage.create({content: `<b>Ataque de lengua</b>: ${args.actor.data.token.name} está ahora @Condition[Enmarañado]`, speaker : {alias: args.attacker.data.token.name}})
}

if (args.type == "skill" &&  args.item.name.includes("Tropienie"))
{
   let sl = this.actor.characteristics.i.bonus
   args.prefillModifiers.slBonus += sl;
}
