wfrp4e.script:
let item = duplicate(this.item)
item.quantity.value--
if (item.quantity.value < 0)
   return ui.notifications.error("No queda más")

this.actor.updateEmbeddedEntity("OwnedItem", item)

let bleeding = this.actor.hasCondition("bleeding")
if (bleeding)
{
   ui.notifications.notify(`Eliminados ${bleeding.flags.wfrp4e.value} Estados de Sangrando`)
   this.actor.removeCondition("bleeding", bleeding.flags.wfrp4e.value)
}

let quantity = this.item.quantity.value - 1
if (quantity < 0)
   return ui.notifications.error("No quedan más pociones")


let wounds = this.actor.status.wounds
if (wounds.value == 0)
  return ui.notifications.error("Sin efecto con 0 Heridas")

ui.notifications.notify(`Curadas ${this.actor.characteristics.t.bonus} Heridas`)

await this.item.update({"data.quantity.value" : quantity})
this.actor.modifyWounds(this.actor.characteristics.t.bonus)

let item = duplicate(this.item)
item.quantity.value--
if (item.quantity.value < 0)
   return ui.notifications.error("No quedan más")

this.actor.updateEmbeddedEntity("OwnedItem", item)

let stunned= this.actor.hasCondition("stunned")
if (stunned)
{
   ui.notifications.notify(`Usunięto 1 Stan Ogłuszenia`)
   this.actor.removeCondition("stunned", 1)
}

let item = this.item.toObject()
item.data.quantity.value--
if (item.data.quantity.value < 0)
   return ui.notifications.error("Nie ma więcej")

this.actor.updateEmbeddedDocument("Item", item)

let fatigued= this.actor.hasCondition("fatigued")
if (fatigued)
{
   ui.notifications.notify(`Usunięto ${fatigued.conditionValue} Stanów Wyczerpania`)
   this.actor.removeCondition("fatigued", fatigued.conditionValue)
}

if (args.type == "characteristic") {
  if (args.item == "wp" || args.item == "t")
    args.prefillModifiers.modifier += 10
  else if (args.item == "ag" || args.item == "i" || args.item == "int")
    args.prefillModifiers.modifier -= 10
}
else if (args.type == "skill") {
  if (args.item.characteristic.key == "wp" || args.item.characteristic.key == "t")
  args.prefillModifiers.modifier += 10
  else if (args.item.characteristic.key == "ag" || args.item.characteristic.key == "i" || args.item.characteristic.key == "int")
    args.prefillModifiers.modifier -= 10
}
else if (args.type == "cast") {
  args.prefillModifiers.modifier -= 10
}
else if (args.type == "channelling") {
  args.prefillModifiers.modifier += 10
}
